import React, { Component } from "react";
import logo from "./logo.svg";
import fontenay from "./fontenay.jpg";
import koh from "./koh.jpg";
import RuPauls from "./RuPauls.jpg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      transitValue: "imageEntre",
      transit2: null,
    };
  }

  render() {
    return (
      <div>
        {this.state.transit2 == null && (
          <img
            onMouseEnter={transite3}
            src={RuPauls}
            className={
              this.state.transitValue == "imageEntre"
                ? "imageEntre"
                : "transit imageEntre"
            }
            alt="RuPauls"
          />
        )}
        {console.log("transit2 ::", this.state.transitValue)}
        <img
          onMouseEnter={transite3}
          src={fontenay}
          className={this.state.transitValue}
          alt="fontenay"
        />
        <img
          onMouseEnter={transite3}
          src={koh}
          className={this.state.transitValue}
          alt="denis"
        />
      </div>
    );
  }
  updateState(newState, runValidation = false) {
    this.setState(newState, () => {
      if (this.context.form && runValidation) {
        this.context.form.runInputValidation(this);
      }
    });
  }
}

function transite3() {
  //console.log("oui AVANT::", this.state.transitValue);
  let transit = "transit";
  this.updateState({ transitValue: transit });
  console.log("oui ::", this.state.transitValue);
  this.updateState({ transit2: "transit imageEntre" });
  return;
}

export default App;
