import React, { useState } from "react";
import logo from "./logo.svg";
import fontenay from "./fontenay.jpg";
import koh from "./koh.jpg";
import RuPauls from "./RuPauls.jpg";
import "./App.css";

let transitValue = "imageEntre";
let transit2 = null;

function App() {
  return <div>{transite(transitValue)}</div>;
  transit2 = "";
}
function transite(zoom) {
  let ez = zoom;
  render(
    <div>
      {transit2 == null && (
        <img
          onMouseEnter={transite3}
          src={RuPauls}
          className={ez == "imageEntre" ? "imageEntre" : "transit imageEntre"}
          alt="RuPauls"
        />
      )}
      {console.log("transit2 ::", zoom)}
      <img
        onMouseEnter={transite3}
        src={fontenay}
        className={transitValue}
        alt="fontenay"
      />
      <img
        onMouseEnter={transite3}
        src={koh}
        className={transitValue}
        alt="denis"
      />
    </div>
  );
}

function transite3() {
  console.log("oui AVANT::", transitValue);
  transitValue = "transit";
  console.log("oui ::", transitValue);
  transit2 = "transit imageEntre";
  return transite(transit2);
}
export default App;
