import React, { useState } from "react";
import logo from "./logo.svg";
import fontenay from "./fontenay.jpg";
import koh from "./koh.jpg";
import RuPauls from "./RuPauls.jpg";
import "./App.css";

let transitValue = "imageEntre transit";

function App() {
  return (
    <div className="App">
      <header className="Split">
        <img
          onMouseEnter={transite}
          src={RuPauls}
          className={transitValue}
          alt="RuPauls"
        />
        <img
          onMouseEnter={transite}
          src={fontenay}
          className={transitValue}
          alt="fontenay"
        />
        <img
          onMouseEnter={transite}
          src={koh}
          className={transitValue}
          alt="denis"
        />
        {console.log("transitValue ::", transitValue)}
      </header>
    </div>
  );
}
const transite = () => {
  console.log("oui AVANT::", transitValue);
  transitValue = "transit";
  console.log("oui ::", transitValue);
  return App();
};
export default App;
