import React, { useState } from "react";
import logo from "./logo.svg";
import fontenay from "./fontenay.jpg";
import koh from "./koh.jpg";
import RuPauls from "./RuPauls.jpg";
import "./App.css";

let transitValue = "imageEntre";

function App() {
  return (
    <div className="App">
      <header className="Split">
        <img src={RuPauls} className="imageEntre" alt="RuPauls" />
        <img src={fontenay} className="imageEntre" alt="fontenay" />
        <div onMouseEnter={transite}>
          <img src={koh} className={transitValue} alt="denis" />
        </div>
      </header>
    </div>
  );
}
const transite = () => {
  console.log("oui ::", transitValue);
  transitValue = "imageEntre-transit";
  return <img src={fontenay} className="imageEntre-transit" alt="denis" />;
};
export default App;
