import React, { Component } from "react";
import logo from "./logo.svg";
import fontenay from "./fontenay.jpg";
import koh from "./koh.jpg";
import RuPauls from "./RuPauls.jpg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      transitValue: null,
      transit2: null,
    };
    this.transite3 = this.transite3.bind(this);
  }

  componentWillMount() {
    this.state.transitValue = "imageEntre";
  }

  render() {
    return (
      <div>
        {this.state.transit2 == null && (
          <img
            onMouseEnter={this.transite3}
            src={RuPauls}
            className={this.state.transitValue}
            alt="RuPauls"
          />
        )}
        {console.log("transit2 ::", this.state.transitValue)}
        <img
          onMouseEnter={this.transite3}
          src={fontenay}
          className={this.state.transitValue}
          alt="fontenay"
        />
        <img
          onMouseEnter={this.transite3}
          src={koh}
          className={this.state.transitValue}
          alt="denis"
        />
      </div>
    );
  }
  transite3() {
    console.log("oui AVANT::", this.state.transitValue);
    let transit = "transit";
    this.setState({ transitValue: transit });
    console.log("oui ::", this.state.transitValue);
    this.setState({ transit2: "transit imageEntre" });
    return;
  }
}

export default App;
